mapper.insert:

insert into folder (id, name, pre_folder_id,
  create_time, update_time, user_id, is_public
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{preFolderId,jdbcType=VARCHAR},
  #{createTime,jdbcType=BIGINT}, #{updateTime,jdbcType=BIGINT}, #{userId,jdbcType=INTEGER},
  #{isPublic,jdbcType=INTEGER}
  )

mapper.select:

select id, name, pre_folder_id, create_time, update_time, user_id
from folder
where id = #{id,jdbcType=INTEGER}

select id, name, pre_folder_id, create_time, update_time, user_id
from folder
where id = 1;

select id, name, pre_folder_id, create_time, update_time, user_id
from folder
where user_id = #{userId} and pre_folder_id = 1;

select id, name, pre_folder_id, create_time, update_time, user_id
from folder
where pre_folder_id = #{preFolderId};
